mapper.select:
SELECT COUNT(*)
FROM t_match mat
WHERE mat.mat_code = #{code}

SELECT mat.mat_id AS id, mat.mat_code AS code, mat.mat_date AS date, mat.mat_description AS description, mat.mat_num_players_min AS numPlayersMin, mat.mat_num_players_max AS numPlayersMax, mat.mat_carpooling_enabled AS carpoolingEnabled, mat.mat_code_sharing_enabled AS codeSharingEnabled, sit.sit_id AS siteId, sit.sit_name AS siteName, sit.sit_address AS siteAddress, sit.sit_phone_number AS sitePhoneNumber, sit.sit_latitude AS siteLatitude, sit.sit_longitude AS siteLongitude, cre.ply_id AS creatorId, cre.ply_surname AS creatorSurname, cre.ply_first_name AS creatorFirstName, cre.ply_email AS creatorEmail, pma.pma_match_id AS matchId, pma.pma_player_id AS playerId, ply.ply_first_name AS playerFirstName, ply.ply_email AS playerEmail, car.car_id AS carId, car.car_num_seats AS carNumSeats FROM t_match mat JOIN t_site sit ON mat.mat_site_id = sit.sit_id JOIN t_player cre ON mat.mat_creator_id = cre.ply_id JOIN t_player_match pma ON mat.mat_id = pma.pma_match_id LEFT JOIN t_player ply ON pma.pma_player_id = ply.ply_id LEFT JOIN t_car car ON pma.pma_car_id = car.car_id WHERE pma.pma_player_id = #{player.id}
AND mat.mat_date >= #{startDate}
AND mat.mat_date <= #{endDate}
ORDER BY mat.mat_date ASC
ORDER BY mat.mat_date DESC

SELECT COUNT(*)
FROM t_player_match pma
WHERE pma.pma_player_id = #{player.id} AND pma.pma_match_id = #{match.id}
